import requests
import json

url = "https://open.admin.shopee.io/api/v2/logistics/download_shipping_document?access_token=access_token&partner_id=partner_id&shop_id=shop_id&sign=sign&timestamp=timestamp"

payload=json.dumps({
  "order_list": [
    {
      "order_sn": "201118BCKPJQQ8",
      "package_number": "2485710696837122445"
    }
  ],
  "shipping_document_type": "NORMAL_AIR_WAYBILL"
})
headers = {
  'Content-Type': 'application/json'
}
response = requests.RPCRequest("POST",url,headers=headers, data=payload, allow_redirects=False)

print(response.text)
