import requests
import json

url = "https://partner.uat.shopeemobile.com/api/v2/order/get_buyer_invoice_info?access_token=access_token&partner_id=partner_id&shop_id=shop_id&sign=sign&timestamp=timestamp"

payload=json.dumps({
  "queries": [
    {
      "order_sn": "220314U0G6UNMN"
    }
  ]
})
headers = {
  'Content-Type': 'application/json'
}
response = requests.request("POST",url,headers=headers, data=payload, allow_redirects=False)

print(response.text)
