import requests
import json

url = "https://partner.uat.shopeemobile.com/api/v2/product/update_tier_variation?access_token=access_token&partner_id=partner_id&shop_id=shop_id&sign=sign&timestamp=timestamp"

payload=json.dumps({
  "item_id": 1000,
  "tier_variation": [
    {
      "name": "-",
      "option_list": [
        {
          "image": {
            "image_id": "-"
          },
          "option": "-"
        }
      ]
    }
  ]
})
headers = {
  'Content-Type': 'application/json'
}
response = requests.request("POST",url,headers=headers, data=payload, allow_redirects=False)

print(response.text)
