import requests
import json

url = "https://partner.shopeemobile.com/api/v2/product/init_tier_variation?access_token=access_token&partner_id=partner_id&shop_id=shop_id&sign=sign&timestamp=timestamp"

payload=json.dumps({
  "model": [
    {
      "gtin_code": "-",
      "model_sku": "SKU",
      "original_price": 38.3,
      "seller_stock": [
        {
          "location_id": "-",
          "stock": 0
        }
      ],
      "weight": 1.1
    }
  ],
  "standardise_tier_variation": [
    {
      "variation_group_id": 2,
      "variation_id": 1,
      "variation_name": "name1",
      "variation_option_list": [
        {
          "image_id": "82becb4830bd2ee90ad6acf8a9dc26d7",
          "variation_option_id": 0,
          "variation_option_name": "option2"
        }
      ]
    }
  ],
  "tier_variation": [
    {
      "name": "Color",
