import requests
import json

url = "https://partner.shopeemobile.com/api/v2/product/update_model?access_token=access_token&partner_id=partner_id&shop_id=shop_id&sign=sign&timestamp=timestamp"

payload=json.dumps({
  "model": [
    {
      "model_sku": "abc",
      "pre_order": {
        "is_pre_order": False
      },
      "weight": 1.1
    }
  ]
})
headers = {
  'Content-Type': 'application/json'
}
response = requests.RPCRequest("POST",url,headers=headers, data=payload, allow_redirects=False)

print(response.text)
